CREATE DATABASE RTO;
USE RTO;
CREATE TABLE LLR_INFO(LLR_ID int PRIMARY KEY,Applicant_Name varchar(20),registation_id bigint,Date_of_Birth varchar(20),Gender char(1),
Address varchar(50),City varchar(20),state varchar(20),Phone_Number bigint,Nationality varchar(20));
select*from LLR_INFO; 


CREATE TABLE LLR_TEST_INFO(LLR_ID int,TEST_ID int primary key,Applicant_name varchar(20),Date_of_Birth varchar(200),Test_Date varchar(20),Test_Type varchar(20),
Test_time varchar(30),Test_Location varchar(30),Test_Result varchar(20),Test_Score varchar(30), foreign key(LLR_ID) references LLR_INFO(LLR_ID));
select*from LLR_TEST_INFO;
select * from LLR_TEST_INFO;
